INSERT INTO `faqs` (`id`, `privacy`, `position`, `question`, `answer`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
(1, 1, NULL, 'জেনেটিক টেস্ট কি ?', 'মানব শরীরের প্রতিটি কোষেই (cell) রয়েছে ক্রোমোজম (chromosome) । ক্রোমোসোম হলো সুতার মতো একটি বস্তু। এই ক্রোমোজমে থাকে অসংখ্য জিন। প্রতিটি কোষে অন্তত ২৫ হাজার জিন থাকে যা শরীরের প্রত্যেকটি কাজের বৈশিষ্টকে বহন করে। এইসকল জিনের গঠনে কোনো ত্রূটি/পরিবর্তন বা কোনো জিনের অনুপস্থিতিকেই জিনগত ত্রূটি বলা হয়। এইসকল ত্রুটি নির্ণয় করতে যেই টেস্ট করা হয় তাকেই জেনেটিক টেস্ট বলে।', 1, 1, '2021-05-15 10:34:25', '2021-09-20 17:00:30'),
(2, 1, NULL, 'জেনেটিক টেস্টটি কিভাবে করা হয় ?', 'জেনেটিক টেস্টের জন্য রোগীর রক্ত নমুনা হিসেবে নেয়া হয়ে থাকে। রক্তের কোষেও ডিএনএ উপস্থিত থাকে। সেই ডিএনএ এর সিকোএন্সিং এর মাধ্যমে জেনেটিক টেস্ট করা হয়। নিউরোজেন illumina এবং Oxford Nanopore Technologies জিনোম সিকোইয়েন্সিং Technology ব্যবহার করে থাকে এবং HPCC Computing ব্যবহার করে জেনেটিক রোগের জিনগত ত্রুটি নির্ণয় করে। ', 1, 1, '2021-05-15 10:35:42', '2021-09-20 17:03:41'),
(3, 1, NULL, 'জেনেটিক টেস্টের রিপোর্ট কি দেশের বাইরে নিয়ে যেতে পারবো ?', 'নিউরোজেনের Genetics and Genomic Medicine Centre (GGMC) থেকে জেনেটিক টেস্টের প্রতিটি রিপোর্ট ACMG (American College Of Medical Genetics and Genomics) গাইডলাইনকে অনুসরণ করে করা হয়। এইসকল রিপোর্ট পৃথিবীর যেকোনপ্রান্তে চিকিৎসার কাজে গ্রহণযোগ্য। নিউরোজেন illumina এবং Oxford Nanopore Technologies জিনোম সিকোইয়েন্সিং Technology ব্যবহার করে থাকে এবং HPCC Computing ব্যবহার করে জেনেটিক রোগের জিনগত ত্রুটি নির্ণয় করে। ', 1, 1, '2021-05-15 10:36:55', '2021-09-20 17:03:50'),
(4, 1, NULL, 'স্পিচ থেরাপি ও অকুপেশনাল থেরাপি কতদিন দিতে হয় ?', 'রোগীর প্রথম এসেসমেন্ট এর পরে থেরাপিস্ট নিজেই একটি ধারণা দিতে পারবেনা যে রোগীর কিরকম উন্নতি হতে পারে এবং কতদিন সময় লাগতে পারে। কারণ একেক রোগীর ক্ষেত্রে উন্নয়ন একেক রকম দেখা যায়।', 1, 1, '2021-05-15 10:39:14', '2021-09-20 17:04:00'),
(5, 1, NULL, 'জেনেটিক কাউন্সেলিং কি ?', 'অটিজম, খিঁচুনি এবং অন্যান্য মানসিক ও বিকাশজনিত সমস্যা ও বিভিন্ন বিরল রোগের জিনগত যে কারণগুলো থাকে এবং এর  চিকিৎসা গ্রহণের আগে ও পরে কি করণীয়, তা অভিজ্ঞ জেনেটিসিস্ট এর দ্বারা গ্রহণ করাকে জেনেটিক কাউন্সেলিং বলে। জেনেটিক কাউন্সেলিং দ্বারা, নিউরোজেনের জেনেটিসিস্ট বা জেনেটিক কাউন্সেলররা একজন পেশেন্ট এর জন্য জেনেটিক টেস্ট ঠিক কতটা গুরুত্বপূর্ণ সেই বিজ্ঞানভিত্তিক ধারণা দিয়ে থাকেন। জেনেটিক কাউন্সেলিং এর মাধ্যমে পরিবারে কোনো নির্দিষ্ট রোগ বংশগতভাবে স্থানান্তরিত হবে কি না সেই পরামর্শ দেয়া হয়। একজন জেনেটিক কাউন্সেলর আপনাকে রোগের ধরণ অনুসারে সঠিক জেনেটিক টেস্টটি নির্বাচন করতে সহযোগিতা করবে এবং কিভাবে এই টেস্ট করানো হয় সে সম্পর্কে বিস্তারিত ধারণা দিবে।', 1, 1, '2021-05-15 10:39:58', '2021-09-20 17:04:09'),
(6, 1, NULL, 'জেনেটিক কাউন্সেলিং কি শিশুদের জন্য করা হয় ?', 'আমাদের ক্লিনিকে যেই কাউন্সেলিংটি করা হয়, সেটি মূলত জেনেটিক কাউন্সেলিং , এখানে জেনেটিক টেস্ট এবং এর গুরুত্ব সম্পর্কে বিস্তারিত রোগীর অবিভাবকদের সাথে আলোচনা করা হয়। ', 1, 1, '2021-05-15 10:40:24', '2021-09-20 17:04:19'),
(7, 1, NULL, 'জেনেটিক টেস্ট কত বছর বয়সে করা যায় ?', 'একজন মানুষের জন্মের পরে যেকোনো বয়সেই এই টেস্ট করা সম্ভব। কারণ জন্মের পরে ডিএনএ এর প্যাটার্ন কখনো পরিবর্তন হয় না। তাই যেকোনো বয়সেই এই টেস্ট করা সম্ভব। জেনেটিক টেস্টের জন্য রোগীর রক্ত নমুনা হিসেবে নেয়া হয়ে থাকে। রক্তের কোষেও ডিএনএ উপস্থিত থাকে। সেই ডিএনএ এর সিকোএন্সিং এর মাধ্যমে জেনেটিক টেস্ট করা হয়। ', 1, 1, '2021-05-15 10:40:45', '2021-09-20 17:04:31'),
(8, 1, NULL, 'জিন কি ?', 'মানব শরীরের প্রতিটি কোষেই (cell) রয়েছে ক্রোমোজম (chromosome) । ক্রোমোসোম হলো সুতার মতো একটি বস্তু। এই ক্রোমোজমে থাকে অসংখ্য জিন। প্রতিটি কোষে অন্তত ২৫ হাজার জিন থাকে যা একজন মানুষের চুলের রং থেকে শুরু করে শরীরের প্রত্যেকটি কাজের বৈশিষ্টকে বহন করে। ', 1, NULL, '2021-09-20 16:59:53', '2021-09-20 16:59:53'),
(9, 1, NULL, 'মিউটেশন বা জিনগত ত্রুটি কাকে বলে ?', 'মানব শরীরের প্রতিটি কোষেই (cell) রয়েছে ক্রোমোজম (chromosome) । ক্রোমোসোম হলো সুতার মতো একটি বস্তু। এই ক্রোমোজমে থাকে অসংখ্য জিন। প্রতিটি কোষে অন্তত ২৫ হাজার জিন থাকে যা একজন মানুষের চুলের রং থেকে শুরু করে শরীরের প্রত্যেকটি কাজের বৈশিষ্টকে বহন করে। এইসকল জিনের গঠনে কোনো ত্রূটি/পরিবর্তন বা কোনো জিনের অনুপস্থিতিকেই জিনগত ত্রূটি বলা হয়। ', 1, NULL, '2021-09-20 17:05:15', '2021-09-20 17:05:15'),
(10, 1, NULL, 'জেনেটিক টেস্ট কিভাবে মানসিক বিকাশ জনিত রোগ শনাক্ত করে ? ', 'পৃথিবীর প্রায় সবগুলো উন্নত দেশের গবেষণায় অটিজম, এপিলেপ্সি ও বিকাশজনিত সমস্যার মুল কারণ যে জেনেটিক সেটা এখন প্রমাণিত ও একটি সায়েন্টিফিক ফ্যাক্ট । আমাদের দেশেও এই সমস্যা গুলোর মুল কারণ জেনেটিক । অটিজম, খিঁচুনি ও শিশুদের বিকাশজনিত সমস্যার কার্যকরী ডায়াগনোসিস হয় জেনেটিক টেস্টের মাধ্যমে । জেনেটিক টেস্ট দ্বারা অল্প বয়সেই এইসকল জিনগত মিউটেশন কে শনাক্ত করলে দ্রুত চিকিৎসার পদক্ষেপ নেয়া সম্ভব।', 1, NULL, '2021-09-20 17:06:38', '2021-09-20 17:06:38'),
(11, 1, NULL, 'জেনেটিক টেস্ট এর প্রাইস তুলনামূলক বেশি কেন ?', 'প্রাথমিক ভাবে জেনেটিক টেস্টকে ব্যায়বহুল মনে হলেও, ভবিষ্যৎ এ এটি অর্থনৈতিকভাবে একটি পরিবার কে সাহায্য করতে পারে। কারণ শিশুর জেনেটিক রোগটি নির্ণয় হওয়ার আগ পর্যন্ত একটি পরিবার বিভিন্ন জাগায় সঠিক ডায়াগনসিস এর জন্য যেয়ে থাকেন, এতে করে পরিবারের ভোগান্তি ও অর্থনৈতিক সমস্যা বাড়তে থাকে।', 1, NULL, '2021-09-20 17:07:14', '2021-09-20 17:07:14');

