INSERT INTO `journeys` (`id`, `privacy`, `identifier`, `title`, `overview`, `image`, `created_by`, `updated_by`, `created_at`, `updated_at`) VALUES
(1, 1, 'যাত্রা শুরু', 'পূর্বের লোগো', NULL, 'images/our-journey/QEWOYO3Q3mEi8fzlu8OPgUdzg0qsOD8LOtZ7zagT.jpg', 1, 1, '2021-07-01 12:59:01', '2021-07-01 13:30:30'),
(2, 1, 'যাত্রা শুরু', 'যেভাবে যাত্রা শুরু', 'নিউরোজেন প্রতিষ্ঠার ধারণাটা সবার আগে এসেছিল ২০১৫ সালের মাঝামাঝিতে। তখন একজন মাত্র বিশেষজ্ঞ ছিলেন, প্রধান বিজ্ঞানী মোহাম্মদ উদ্দিন ডাফিল। আমাদের ব্যবস্থাপনা পরিচালক, চিফ অপারেটিং অফিসার ও একজন ক্লিনিক্যাল বিজ্ঞানী মিলে এই নিউরোজেনের ধারণাটাকে একটি প্রতিষ্ঠানে রূপ দেওয়ার লক্ষ্যে কাজ করা শুরু করেন। শুরুতে বেশ ঝামেলায় পড়তে হয়েছে আমাদের কেননা জেনেটিক টেস্ট এবং এর উপকারিতা সম্পর্কে সাধারণ মানুষের তেমন কোন ধারনা ছিল না। তারপরও আমরা নিরলস কাজ করে গিয়েছি। অবশ্য তখনো ভাবতে পারিনি যে একদিন আমাদের প্রতিষ্ঠান এত বড় একটা প্ল্যাটফর্ম প্রতিষ্ঠা করতে সক্ষম হবে। ', NULL, 1, 1, '2021-07-01 13:03:29', '2021-07-01 13:36:21'),
(3, 1, 'সম্প্রসারণ', 'প্রাথমিক অবকাঠামোগত উন্নয়ন', 'প্রথমে অনেক কম যন্ত্রপাতি ছিল, যার মধ্যে উল্লেখযোগ্য ছিল WPS অনুমোদিত ADOS 2 kit, ল্যাপটপ, ডেস্কটপ ও অন্যান্য অফিসের সরঞ্জামাদি। আমরা বিভিন্ন আন্তর্জাতিক সংস্থার সাথে সম্পৃক্ত হতে থাকলাম আস্তে আস্তে। ফলে জেনেটিক জগতের নিত্য নতুন প্রযুক্তি সম্পর্কে জানা সম্ভব হয়েছিল এবং আমাদের প্রতিষ্ঠানের অবকাঠামোগত উন্নয়ন ত্বরান্বিত হতে থাকলো।', NULL, 1, 1, '2021-07-01 13:12:36', '2021-07-01 13:46:55'),
(4, 1, 'সম্প্রসারণ', 'সার্ভিস লাইন প্রসারন', '২০১৬ সালের অক্টোবরে সর্বপ্রথম নিউরোজেন সেবা দেওয়া শুরু করে, তখন মাত্র আটজন কর্মী ছিলেন। যাদের মধ্যে দুইজন ব্যবস্থাপনা পরিচালক, একজন চিফ অপারেটিং অফিসার, একজন বিশেষজ্ঞ বিজ্ঞানী, একজন ক্লিনিক্যাল বিজ্ঞানী, দুইজন সহকারী ক্লিনিক্যাল সাইকোলোজিস্ট ও একজন MLSS.', NULL, 1, 1, '2021-07-01 13:12:53', '2021-07-01 13:36:00'),
(5, 1, 'এগিয়ে চলা', 'আন্তর্জাতিক সংস্থার সার্টিফিকেশন লাভ', 'নিউরোজেন এর ক্রমান্নোতি অব্যাহত ছিল সম্পূর্ণ ২০১৬ সাল জুড়ে। আমরা WPS এর পর Illumina সংগঠনের সাথে সম্পৃক্ত হলাম এবার। ফলে আমাদের অটিজম অ্যাসেসমেন্ট, থেরাপিউটিক্স এবং জেনেটিক টেস্ট সেকশনটা খুব স্ট্রং হোল এবার। আমাদের পূর্বে কোন প্রতিষ্ঠান এদেশে অটিজম নির্ণয়ে জেনেটিক টেস্টের ব্যবহার করেনি। ফলে বাংলাদেশের অটিজমের চিকিৎসায় যোগ হলো এক নতুন মাত্রা।', NULL, 1, NULL, '2021-07-01 13:37:07', '2021-07-01 13:37:07'),
(6, 1, 'এগিয়ে চলা', 'ল্যাবরেটরি', NULL, 'images/our-journey/DFY8NQg4zfaZ0v3gpfqaQc3bksr9k3v5xa2JAjwI.jpg', 1, NULL, '2021-07-01 13:40:00', '2021-07-01 13:40:00'),
(7, 1, 'নতুনত্ব', 'ল্যাবরেটরি', NULL, 'images/our-journey/DGf1C5AqDnHQcxG7eKdC5jlZayoiB1HGM9cvVdsu.png', 1, NULL, '2021-07-01 13:40:51', '2021-07-01 13:40:51'),
(8, 1, 'নতুনত্ব', 'এগিয়ে চলা', 'আমাদের অটিজম ডিপার্ট্মেন্ট এর কার্যক্রম শুরু হলেও প্রথমে শুধু স্পিচ থেরাপি এবং সাইকোলজি টেস্ট করা হতো। কিন্তু প্রথম জেনেটিক টেস্টটি করার জন্য আমাদের আরও অপেক্ষা করতে হয়েছে। আমাদের ADOS 2 পরীক্ষার জন্য প্রথম ক্লায়েন্ট আসে নিউরোজেনের কাছে অক্টোবর মাসের ৮ তারিখে। এই পরীক্ষার ফলাফল পজিটিভ ছিল যার অর্থ শিশুটির অটিজম ছিল। এরপর আমরা আরও বেশ কিছু শিশুর অটিজম জেনেটিক টেস্টিং করেছি। সমস্যা ধরা পড়ার তার সমাধানে আমরা বিভিন্ন থেরাপি দিতাম শিশুদের। পাশাপাশি শিশুর পিতা-মাতাকে জেনেটিক কাউন্সেলিং করতাম।', NULL, 1, NULL, '2021-07-01 13:42:07', '2021-07-01 13:42:07'),
(9, 1, 'নতুনত্ব', 'নতুন সার্ভিস', '২০১৭ সালের জানুয়ারি থেকে আমরা নতুন নতুন সেবা দেওয়া শুরু করি, যার জন্য নতুন সরঞ্জামাদি আমাদের গবেষণাগারে ২০১৬ সালের নভেম্বর মাস থেকেই আসতে থাকে। আমরা ইতিমধ্যে কান্সার ডিপার্ট্মেন্ট প্রতিষ্ঠা করলাম। আমাদের এখানে ব্রেস্ট ক্যান্সার এবং ওভারিয়ান ক্যান্সারের জেনেটিক টেস্টিং প্রক্রিয়া শুরু হয়ে গেল। পাশাপাশি ফুসফুসের ক্যান্সারের জন্য দায়ী জিন খুঁজে বের করার প্রযুক্তি নিয়ে এলাম আমরা। বিভিন্ন দুর্লভ এবং কমপ্লেক্স জেনেটিক ডিজিসের চিকিৎসা শুরু করলাম এরপর আমরা। আস্তে আস্তে নিউরোজেন সম্পর্কে মানুষের মাঝে সচেতনতা বৃদ্ধি পাচ্ছিলো।', NULL, 1, NULL, '2021-07-01 13:42:35', '2021-07-01 13:42:35'),
(10, 1, 'অর্জন', 'যোগ হলো এক নতুন মাত্রা', 'আমাদের সাফল্যের মধ্যে রয়েছে –\n– বাংলাদেশের সর্বপ্রথম “বৃক্ষমানব” বালিকার সম্পূর্ণ জিনোম সিকোয়েন্সিং আমরাই সর্বপ্রথম করি\n– প্রায় ৪০০টার মত ADOS 2 পরীক্ষা করেছি আমরা\n– প্রায় ২০টার মত আইকিউ টেস্ট করেছে নিউরোজেন\n– ৫০টার মত array test করেছে নিউরোজেন\n– নিউরোজেনের BC plus panel এর অধীনে ১২ টা স্তন ক্যান্সারের পরীক্ষা করা হয়েছে\nদুটো বিখ্যাত আন্তর্জাতিক প্রতিষ্ঠানের সাথে নিউরোজেন যুক্ত – ইলুমিনা ও ওয়েস্টার্ন সাইকোলোজিক্যাল সার্ভিস', NULL, 1, NULL, '2021-07-01 13:43:18', '2021-07-01 13:43:18');

